import { makeAutoObservable, autorun } from "mobx";
import * as types from '../declarations/types';
import api from "../services/API";

export class Store {
    constructor() {
        makeAutoObservable(this);
        autorun(() => {
            if (this.search=== "F") {
                console.log("Escolheu a letra F");
                this.poderDeLuta = 99999;
            } else if(this.search === "A"){ 
                console.log("Escolheu a letra A")
                this.poderDeLuta = 2;
            } else {
                console.log("Escolheu a letra D")
            }
        })
    };
    public movies: types.Movie[] = [];
    public  loading: boolean = false;
    public  search: string = '';

    public setSearch(search: string) {
        this.search = search;
    };
    public setMovies(movies: types.Movie[]) {
        this.movies = movies;
    };

    public  setLoading(loading: boolean) {
        this.loading = loading;
    };

    public  fetchMovies = async () => {
        if (this.loading) {
            return;
        };
        this.setLoading(true);

            try {
                const movies = await api.getMovies(this.search);
                this.setMovies(movies);
            } catch (e) {
                console.error(e);
            } finally {
                this.setLoading(false);
            };
    };

	public grito = "tatakae";
	public poderDeLuta = 1;
	public temporada = 1;
	public dano = 0;

	public get calcularDano() {
		return this.poderDeLuta * this.temporada;
	}

};
